package ksyjava;

public class ForExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int agv;
		for(int x = 3;x<8;x++) {
			
			for(int y = 1;y<10;y++) {
				agv = x*y;
				
				System.out.print(x+"X"+y+"="+agv+" ");
				
			}
			
			System.out.println();
		}
			
	}

}
